#!/bin/bash
POSTGRES_USER=postgres
POSTGRES_DB=api-db
function populate_table () {
    echo "Populating  $1 Table..."
    docker-compose exec db psql -U $POSTGRES_USER -d $POSTGRES_DB -c "$2"
    if [ $? -eq 0 ]; then
        echo "$1 table successfully populated.";
    else
        echo "An error occured while populating $1 Table!!!!";
        exit
    fi
}



